import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Layout from './layout';

const UserDetail = () => {
  const { id } = useParams(); // Get user ID from the URL
  const [user, setUser] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(true);

  // Fetch user details when the component mounts
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`/api/users/${id}`); // Correct API endpoint
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user details:', error);
        setErrorMessage('Error fetching user details');
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [id]);

  // If loading, show a spinner or loading message
  if (loading) {
    return (
      <Layout>
        <div className="container text-center">
          <h2>Loading user details...</h2>
        </div>
      </Layout>
    );
  }

  if (errorMessage) {
    return (

        <div className="container">
          <div className="alert alert-danger">{errorMessage}</div>
        </div>

    );
  }

  return (
      <div className="container my-5">
        <h2>User Details</h2>
        {user && (
          <ul className="list-group">
            <li className="list-group-item"><strong>First Name:</strong> {user.firstName}</li>
            <li className="list-group-item"><strong>Last Name:</strong> {user.lastName}</li>
            <li className="list-group-item"><strong>Date of Birth:</strong> {new Date(user.dateOfBirth).toLocaleDateString()}</li>
            <li className="list-group-item"><strong>Email:</strong> {user.email}</li>
            <li className="list-group-item"><strong>Phone Number:</strong> {user.phoneNumber}</li>
            <li className="list-group-item"><strong>Address 1:</strong> {user.address1}</li>
            <li className="list-group-item"><strong>Address 2:</strong> {user.address2}</li>
            <li className="list-group-item"><strong>City:</strong> {user.city}</li>
            <li className="list-group-item"><strong>Postal Code:</strong> {user.postalCode}</li>
            <li className="list-group-item"><strong>Country:</strong> {user.country}</li>
            <li className="list-group-item"><strong>User Notes:</strong> {user.userNotes}</li>
          </ul>
        )}
        <Link to="/viewUser" className="btn btn-primary mt-3">Back to User List</Link>
      </div>
  );
};

export default UserDetail;
