import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, TextInput, Pane, Heading, Alert } from 'evergreen-ui';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Dummy check: If email and password match dummy values
    if (email === 'test@estexample.com' && password === 'password123') {
      navigate('/viewUser');  // Redirect to the user list page
    } else {
      setErrorMessage('Invalid email or password');
    }
  };

  return (
    <div className="login-page">
      {/* Flexbox container for centering the login */}
      <Pane 
        display="flex" 
        justifyContent="center" 
        alignItems="flex-start" 
        height="100vh"  // Full viewport height
        paddingTop="2%"  // Position container 20% from the top
        backgroundColor="#f1f1f1"
      >
        <Pane
          width={500}  // Adjust width for a slightly larger form
          padding={40}  // Increased padding for a bit more space around content
          display="flex"
          flexDirection="column"
          alignItems="center"
          boxShadow="0 4px 8px rgba(0, 0, 0, 0.15)"  // Enhanced shadow effect
          borderRadius={8}
          backgroundColor="#fff"
        >
          <Heading size={600} marginBottom={20}>Login</Heading>

          {/* Show error message if exists */}
          {errorMessage && <Alert intent="danger" title={errorMessage} marginBottom={20} />}

          <form onSubmit={handleLogin}>
            <Pane marginBottom={15}>
              <TextInput
                id="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                width="100%"
                height={50}  // Increased height for a longer input field
                marginBottom={10}
                required
              />
            </Pane>

            <Pane marginBottom={25}>
              <TextInput
                id="password"
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                width="100%"
                height={50}  // Increased height for a longer input field
                marginBottom={10}
                required
              />
            </Pane>

            <Button
              type="submit"
              appearance="primary"
              intent="success"
              width="100%"
              height={45}  // Slightly taller button for a better click area
              marginBottom={10}
            >
              Login
            </Button>
          </form>
        </Pane>
      </Pane>
    </div>
  );
};

export default Login;
