import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import Layout from './layout';
import { AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/styles/ag-grid.css';
import 'ag-grid-community/styles/ag-theme-alpine.css';

const ViewUser = () => {
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate(); // For programmatic navigation

  // Fetch all users when the component mounts
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('/api/users'); // Correct API endpoint
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching users:', error);
        setErrorMessage('Error fetching user data');
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // Handle delete user action
  const handleDeleteUser = async (id) => {
    try {
      await axios.delete(`/api/deleteUser/${id}`); // Correct API endpoint
      setUsers((prevUsers) => prevUsers.filter(user => user._id !== id)); // Remove user from the list after deletion
    } catch (error) {
      console.error('Error deleting user:', error);
      setErrorMessage('Error deleting user');
    }
  };

  const columnDefs = [
    { headerName: "First Name", field: "firstName" },
    { headerName: "Last Name", field: "lastName" },
    { headerName: "Date of Birth", field: "dateOfBirth", valueFormatter: (params) => new Date(params.value).toLocaleDateString() },
    { headerName: "Email", field: "email" },
    {
      headerName: "Actions",
      cellRendererFramework: (params) => (
        <div className="dropdown">
          <button
            className="btn btn-warning dropdown-toggle"
            type="button"
            id={`dropdownMenuButton${params.data._id}`}
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
          >
            Actions
          </button>
          <div className="dropdown-menu" aria-labelledby={`dropdownMenuButton${params.data._id}`}>
            <Link to={`/updateUser/${params.data._id}`} className="dropdown-item">
              Update
            </Link>
            <button
              className="dropdown-item btn btn-danger"
              onClick={() => handleDeleteUser(params.data._id)}
            >
              Delete
            </button>
            <button
              className="dropdown-item"
              onClick={() => navigate(`/userDetail/${params.data._id}`)}
            >
              View Details
            </button>
          </div>
        </div>
      )
    }
  ];

  // If loading, show a spinner or loading message
  if (loading) {
    return (
      <Layout>
        <div className="container text-center">
          <h2>Loading users...</h2>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="container-fluid my-5">
        <h2>User List</h2>
        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

        {/* AG-Grid component */}
        <div className="ag-theme-alpine" style={{ height: 400, width: '100%' }}>
          <AgGridReact
            columnDefs={columnDefs}
            rowData={users}
            pagination={true}
            domLayout="autoHeight"
            enableBrowserTooltips={true}
            rowSelection="single"
            onRowClicked={(event) => navigate(`/userDetail/${event.data._id}`)} // Navigate to user details on row click
          />
        </div>

        <Link to="/addUser" className="btn btn-success mt-3">
          Add User
        </Link>
      </div>
    </Layout>
  );
};

export default ViewUser;
