import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Layout = ({ children }) => {
  const navigate = useNavigate(); // Use navigate hook for navigation

  return (
    <div>
      <header className="d-flex justify-content-between align-items-center w-100 p-3 bg-light position-sticky top-0 z-index-10">
        <button
          className="view-users-btn btn btn-link"
          onClick={() => navigate('/viewUser')} // Use navigate for routing
        >
          View Users
        </button>
        <h1 className="mx-auto">User Management System</h1>
      </header>

      <main>
        {children} {/* This will render the page-specific content */}
      </main>

      <footer className="text-center p-3 bg-light mt-auto">
        <p>&copy; 2024 Kisan Rai, Student Number C0910925. All rights reserved.</p>
      </footer>
    </div>
  );
};
