import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom'; // useParams to get the user ID from the URL
import Layout from './layout'; // Import Layout component

const UpdateUser = () => {
  const { id } = useParams(); // Get the user ID from the URL
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    phoneNumber: '',
    email: '',
    address1: '',
    address2: '',
    city: '',
    postalCode: '',
    country: '',
    userNotes: '',
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [loading, setLoading] = useState(true); // Loading state
  const navigate = useNavigate();

  // Fetch user details when the component mounts
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`/api/users/${id}`); // Fetch user by ID from the backend
        setFormData(response.data); // Populate form with fetched user data
      } catch (error) {
        setErrorMessage('Error loading user details.');
        console.error('Error fetching user details:', error);
      } finally {
        setLoading(false); // Set loading to false after data is fetched
      }
    };

    fetchUser();
  }, [id]); // Re-run if the user ID changes

  // Handle form field change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission for updating user
  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');

    try {
      const response = await axios.put(`/api/updateUser/${id}`, formData); // Send PUT request to update user
      setSuccessMessage('User updated successfully!');
      setTimeout(() => navigate('/viewUser'), 2000); // Redirect after 2 seconds to view user page
    } catch (error) {
      setErrorMessage(error.response?.data || 'An error occurred while updating the user');
      console.error('Error updating user:', error);
    }
  };

  // If loading, show loading message
  if (loading) {
    return (
      <Layout>
        <div className="container text-center">
          <h2>Loading user details...</h2>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="container">
        <h2>Update User</h2>
        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
        {successMessage && <div className="alert alert-success">{successMessage}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>First Name</label>
            <input
              type="text"
              className="form-control"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              className="form-control"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Date of Birth</label>
            <input
              type="date"
              className="form-control"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              className="form-control"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Address 1</label>
            <input
              type="text"
              className="form-control"
              name="address1"
              value={formData.address1}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Address 2</label>
            <input
              type="text"
              className="form-control"
              name="address2"
              value={formData.address2}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>City</label>
            <input
              type="text"
              className="form-control"
              name="city"
              value={formData.city}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Postal Code</label>
            <input
              type="text"
              className="form-control"
              name="postalCode"
              value={formData.postalCode}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Country</label>
            <input
              type="text"
              className="form-control"
              name="country"
              value={formData.country}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>User Notes</label>
            <textarea
              className="form-control"
              name="userNotes"
              value={formData.userNotes}
              onChange={handleChange}
            ></textarea>
          </div>
          <button type="submit" className="btn btn-primary">Update User</button>
        </form>
      </div>
    </Layout>
  );
};

export default UpdateUser;
