import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { Pane, Heading, Alert, Button, Textarea, Text } from 'evergreen-ui';

const UserDetail = () => {
  const { id } = useParams(); // Get the user ID from the URL
  const [user, setUser] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // Use navigate hook for redirection

  // Fetch the user details when the component mounts
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await axios.get(`/api/users/${id}`);
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user details:', error);
        setErrorMessage('Error fetching user details');
      }
    };

    fetchUserDetails();
  }, [id]);

  // Handle delete user action
  const handleDeleteUser = async () => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        const response = await axios.delete(`/api/deleteUser/${id}`);
        if (response.status === 200) {
          navigate('/viewUser'); // Redirect to the user list page after deletion
        }
      } catch (error) {
        console.error('Error deleting user:', error);
        setErrorMessage('Error deleting user');
      }
    }
  };

  if (!user) {
    return (
      <Pane display="flex" justifyContent="center" alignItems="center" height="100vh">
        <Heading size={500}>Loading...</Heading>
      </Pane>
    );
  }

  return (
    <Pane
      className="container"
      marginY={24}
      maxWidth={800}
      marginX="auto"
      padding={24}
      borderRadius={8}
      elevation={1}
      background="tint1"
    >
      <Pane display="flex" justifyContent="space-between" alignItems="center" marginBottom={16}>
        <Heading size={600}>User Details</Heading>
        <Button appearance="minimal" onClick={() => navigate('/viewUser')}>
          View Users
        </Button>
      </Pane>

      {errorMessage && (
        <Alert intent="danger" marginBottom={16} title="Error">
          {errorMessage}
        </Alert>
      )}

      <Pane display="flex" flexDirection="column" gap={16}>
        {/* User Details */}
        <Pane display="flex" justifyContent="space-between">
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              First Name
            </Text>
            <Text>{user.firstName}</Text>
          </Pane>
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              Last Name
            </Text>
            <Text>{user.lastName}</Text>
          </Pane>
        </Pane>

        <Pane display="flex" justifyContent="space-between">
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              Date of Birth
            </Text>
            <Text>{new Date(user.dateOfBirth).toLocaleDateString()}</Text>
          </Pane>
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              Phone Number
            </Text>
            <Text>{user.phoneNumber}</Text>
          </Pane>
        </Pane>

        <Pane display="flex" justifyContent="space-between">
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              Email
            </Text>
            <Text>{user.email}</Text>
          </Pane>
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              Address 1
            </Text>
            <Text>{user.address1}</Text>
          </Pane>
        </Pane>

        <Pane display="flex" justifyContent="space-between">
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              Address 2
            </Text>
            <Text>{user.address2 || 'N/A'}</Text>
          </Pane>
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              City
            </Text>
            <Text>{user.city}</Text>
          </Pane>
        </Pane>

        <Pane display="flex" justifyContent="space-between">
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              Postal Code
            </Text>
            <Text>{user.postalCode}</Text>
          </Pane>
          <Pane flex={1}>
            <Text size={400} fontWeight="bold">
              Country
            </Text>
            <Text>{user.country}</Text>
          </Pane>
        </Pane>

        {/* User Notes */}
        <Pane>
          <Text size={400} fontWeight="bold">
            User Notes
          </Text>
          <Textarea
            value={user.userNotes}
            readOnly
            rows={3}
            border="default"
            background="tint2"
          />
        </Pane>

        {/* Buttons */}
        <Pane display="flex" justifyContent="flex-end" gap={16}>
          <Button
            appearance="primary"
            intent="success"
            onClick={() => navigate(`/updateUser/${user._id}`)}
          >
            Update User
          </Button>
          <Button appearance="primary" intent="danger" onClick={handleDeleteUser}>
            Delete User
          </Button>
        </Pane>
      </Pane>
    </Pane>
  );
};

export default UserDetail;
