import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, Link, useNavigate } from 'react-router-dom';
import Layout from './layout'; // Import Layout component

const UserDetail = () => {
  const { id } = useParams(); // Get the user ID from the URL
  const [user, setUser] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // Use navigate hook for redirection

  // Fetch the user details when the component mounts
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await axios.get(`/api/users/${id}`); // Ensure correct API endpoint
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user details:', error);
        setErrorMessage('Error fetching user details');
      }
    };

    fetchUserDetails();
  }, [id]);

  // Handle delete user action
  const handleDeleteUser = async () => {
    if (window.confirm('Are you sure you want to delete this user?')) { // Confirmation prompt
      try {
        const response = await axios.delete(`/api/deleteUser/${id}`); // Correct API endpoint for deleting a user
        if (response.status === 200) {
          console.log('User deleted successfully');
          navigate('/viewUser'); // Redirect to the user list page after deletion
        }
      } catch (error) {
        console.error('Error deleting user:', error);
        setErrorMessage('Error deleting user');
      }
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">User Details</h2>
      {errorMessage && <div className="alert alert-danger text-center">{errorMessage}</div>}

      <div className="user-detail-container">
        {/* User Information */}
        <div className="user-info">
          <div className="info-box">
            <strong>First Name</strong>
            <p>{user.firstName}</p>
          </div>
          <div className="info-box">
            <strong>Last Name</strong>
            <p>{user.lastName}</p>
          </div>
        </div>

        <div className="user-info">
          <div className="info-box">
            <strong>Date of Birth</strong>
            <p>{new Date(user.dateOfBirth).toLocaleDateString()}</p>
          </div>
          <div className="info-box">
            <strong>Address 1</strong>
            <p>{user.address1}</p>
          </div>
        </div>

        <div className="user-info">
          <div className="info-box">
            <strong>Address 2</strong>
            <p>{user.address2}</p>
          </div>
          <div className="info-box">
            <strong>City</strong>
            <p>{user.city}</p>
          </div>
        </div>

        <div className="user-info">
          <div className="info-box">
            <strong>Postal Code</strong>
            <p>{user.postalCode}</p>
          </div>
          <div className="info-box">
            <strong>Country</strong>
            <p>{user.country}</p>
          </div>
        </div>

        <div className="user-info">
          <div className="info-box">
            <strong>Phone Number</strong>
            <p>{user.phoneNumber}</p>
          </div>
          <div className="info-box">
            <strong>Email</strong>
            <p>{user.email}</p>
          </div>
        </div>

        {/* User Notes */}
        <div className="info-box full-width">
          <strong>Note</strong>
          <textarea className="note-field" readOnly value={user.userNotes} />
        </div>

        {/* Buttons */}
        <div className="button-group text-center">
          <Link to={`/updateUser/${user._id}`} className="btn btn-primary mr-2">
            Update User
          </Link>
          <button className="btn btn-danger" onClick={handleDeleteUser}>
            Delete User
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserDetail;
