import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Layout from './layout'; // Import Layout component

const UpdateUser = () => {
  const { id } = useParams(); // Get user ID from URL
  const navigate = useNavigate(); // For navigation after update

  // Initialize form state
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    address1: '',
    address2: '',
    city: '',
    postalCode: '',
    country: '',
    phoneNumber: '',
    email: '',
    userNotes: ''
  });

  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch user data when the component loads
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Ensure correct API endpoint
        const response = await axios.get(`/api/viewUser/${id}`);
        setFormData(response.data); // Set existing user data to form fields
      } catch (error) {
        console.error('Error fetching user data:', error);
        setErrorMessage('Error loading user details.');
      }
    };

    if (id) {
      fetchUserData(); // Fetch user data only if ID is present
    } else {
      setErrorMessage('User ID not found.');
    }
  }, [id]); // Fetch data when the ID changes

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value }); // Update state when form fields change
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');

    try {
      await axios.put(`/api/updateUser/${id}`, formData); // Update the user data
      setSuccessMessage('User updated successfully!');
      setTimeout(() => navigate('/viewUser'), 2000); // Redirect to the user list after 2 seconds
    } catch (error) {
      console.error('Error updating user:', error);
      setErrorMessage(
        error.response?.data || 'An error occurred while updating the user'
      );
    }
  };

  return (
    <Layout>
      <div className="container my-5">
        <h2>Update User</h2>
        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
        {successMessage && <div className="alert alert-success">{successMessage}</div>}

        <form onSubmit={handleSubmit}>
          {/* First Name */}
          <div className="row">
            <div className="col-md-6 mb-3">
              <label htmlFor="firstName" className="form-label">
                First Name
              </label>
              <input
                type="text"
                className="form-control"
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                required
              />
            </div>
            {/* Last Name */}
            <div className="col-md-6 mb-3">
              <label htmlFor="lastName" className="form-label">
                Last Name
              </label>
              <input
                type="text"
                className="form-control"
                id="lastName"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          {/* Date of Birth */}
          <div className="row">
            <div className="col-md-6 mb-3">
              <label htmlFor="dateOfBirth" className="form-label">
                Date of Birth
              </label>
              <input
                type="date"
                className="form-control"
                id="dateOfBirth"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={handleChange}
                required
              />
            </div>
            {/* Phone Number */}
            <div className="col-md-6 mb-3">
              <label htmlFor="phoneNumber" className="form-label">
                Phone Number
              </label>
              <input
                type="text"
                className="form-control"
                id="phoneNumber"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          {/* Email */}
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          {/* Address1 */}
          <div className="mb-3">
            <label htmlFor="address1" className="form-label">
              Address 1
            </label>
            <input
              type="text"
              className="form-control"
              id="address1"
              name="address1"
              value={formData.address1}
              onChange={handleChange}
              required
            />
          </div>

          {/* Address2 */}
          <div className="mb-3">
            <label htmlFor="address2" className="form-label">
              Address 2
            </label>
            <input
              type="text"
              className="form-control"
              id="address2"
              name="address2"
              value={formData.address2}
              onChange={handleChange}
            />
          </div>

          {/* City */}
          <div className="row">
            <div className="col-md-6 mb-3">
              <label htmlFor="city" className="form-label">
                City
              </label>
              <input
                type="text"
                className="form-control"
                id="city"
                name="city"
                value={formData.city}
                onChange={handleChange}
                required
              />
            </div>
            {/* Postal Code */}
            <div className="col-md-6 mb-3">
              <label htmlFor="postalCode" className="form-label">
                Postal Code
              </label>
              <input
                type="text"
                className="form-control"
                id="postalCode"
                name="postalCode"
                value={formData.postalCode}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          {/* Country */}
          <div className="mb-3">
            <label htmlFor="country" className="form-label">
              Country
            </label>
            <input
              type="text"
              className="form-control"
              id="country"
              name="country"
              value={formData.country}
              onChange={handleChange}
              required
            />
          </div>

          {/* User Notes */}
          <div className="mb-3">
            <label htmlFor="userNotes" className="form-label">
              User Notes
            </label>
            <textarea
              className="form-control"
              id="userNotes"
              name="userNotes"
              rows="3"
              value={formData.userNotes}
              onChange={handleChange}
            ></textarea>
          </div>

          <button type="submit" className="btn btn-primary">
            Update User
          </button>
          <button
            type="button"
            className="btn btn-secondary ms-3"
            onClick={() => navigate('/viewUser')}
          >
            Cancel
          </button>
        </form>
      </div>
    </Layout>
  );
};

export default UpdateUser;
