import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, Link, useNavigate } from 'react-router-dom';
import Layout from './layout'; // Import Layout component

const UserDetail = () => {
  const { id } = useParams(); // Get the user ID from the URL
  const [user, setUser] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // Use navigate hook for redirection

  // Fetch the user details when the component mounts
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await axios.get(`/api/users/${id}`); // Ensure correct API endpoint
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user details:', error);
        setErrorMessage('Error fetching user details');
      }
    };

    fetchUserDetails();
  }, [id]);

  // Handle delete user action
  const handleDeleteUser = async () => {
    if (window.confirm('Are you sure you want to delete this user?')) { // Confirmation prompt
      try {
        const response = await axios.delete(`/api/users/${id}`); // Ensure correct API endpoint
        if (response.status === 200) {
          // Redirect to the user list page after successful deletion
          navigate('/viewUser');
        }
      } catch (error) {
        console.error('Error deleting user:', error);
        setErrorMessage('Error deleting user');
      }
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container my-5">
      <h2>User Details</h2>
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

      <div className="row d-flex justify-content-center">
        <div className="col-12 col-md-6">
          <dl>
            <dt>First Name</dt>
            <dd>{user.firstName}</dd>
          </dl>
        </div>
        <div className="col-12 col-md-6">
          <dl>
            <dt>Last Name</dt>
            <dd>{user.lastName}</dd>
          </dl>
        </div>
      </div>

      <div className="row d-flex justify-content-center">
        <div className="col-12 col-md-6">
          <dl>
            <dt>Date of Birth</dt>
            <dd>{new Date(user.dateOfBirth).toLocaleDateString()}</dd> {/* Use toLocaleDateString */}
          </dl>
        </div>
        <div className="col-12 col-md-6">
          <dl>
            <dt>Address 1</dt>
            <dd>{user.address1}</dd>
          </dl>
        </div>
      </div>

      <div className="row d-flex justify-content-center">
        <div className="col-12 col-md-6">
          <dl>
            <dt>Address 2</dt>
            <dd>{user.address2}</dd>
          </dl>
        </div>
        <div className="col-12 col-md-6">
          <dl>
            <dt>City</dt>
            <dd>{user.city}</dd>
          </dl>
        </div>
      </div>

      <div className="row d-flex justify-content-center">
        <div className="col-12 col-md-6">
          <dl>
            <dt>Postal Code</dt>
            <dd>{user.postalCode}</dd>
          </dl>
        </div>
        <div className="col-12 col-md-6">
          <dl>
            <dt>Country</dt>
            <dd>{user.country}</dd>
          </dl>
        </div>
      </div>

      <div className="row d-flex justify-content-center">
        <div className="col-12 col-md-6">
          <dl>
            <dt>Phone Number</dt>
            <dd>{user.phoneNumber}</dd>
          </dl>
        </div>
        <div className="col-12 col-md-6">
          <dl>
            <dt>Email</dt>
            <dd>{user.email}</dd>
          </dl>
        </div>
      </div>

      <div className="row d-flex justify-content-center">
        <div className="col-12">
          <dl>
            <dt>Note</dt>
            <dd>
              <textarea
                className="note-field"
                readOnly
                value={user.userNotes}
                style={{ resize: 'none', height: '150px', width: '100%' }}
              />
            </dd>
          </dl>
        </div>
      </div>

      <div className="button-group d-flex justify-content-center">
        <Link to={`/updateUser/${user._id}`} className="btn btn-primary mr-2">
          Update User
        </Link>
        <button className="btn btn-danger" onClick={handleDeleteUser}>
          Delete User
        </button>
      </div>
    </div>
  );
};

export default UserDetail;
