import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Layout from './layout'; // Import Layout component
import { AgGridReact } from 'ag-grid-react'; // Import AG Grid
import 'ag-grid-community/styles/ag-grid.css'; // AG Grid styles
import 'ag-grid-community/styles/ag-theme-alpine.css'; // AG Grid theme styles

const ViewUser = () => {
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(true); // Loading state

  // Fetch all users when the component mounts
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('/api/users'); // Correct API endpoint
        console.log('Fetched users:', response.data); // Log the response data
        if (Array.isArray(response.data)) {
          setUsers(response.data); // Set users in state
        } else {
          setErrorMessage('Data format is incorrect');
        }
      } catch (error) {
        console.error('Error fetching users:', error);
        setErrorMessage('Error fetching user data');
      } finally {
        setLoading(false); // Set loading to false after data is fetched
      }
    };

    fetchUsers();
  }, []);

  // Handle delete user action
  const handleDeleteUser = async (id) => {
    try {
      await axios.delete(`/api/users/${id}`); // Correct API endpoint for deletion
      setUsers((prevUsers) => prevUsers.filter(user => user._id !== id)); // Remove user from the list after deletion
    } catch (error) {
      console.error('Error deleting user:', error);
      setErrorMessage('Error deleting user');
    }
  };

  const columnDefs = [
    { headerName: "First Name", field: "firstName" },
    { headerName: "Last Name", field: "lastName" },
    { headerName: "Date of Birth", field: "dateOfBirth", valueFormatter: (params) => new Date(params.value).toLocaleDateString() },
    { headerName: "Address 1", field: "address1" },
    { headerName: "Address 2", field: "address2" },
    { headerName: "City", field: "city" },
    { headerName: "Postal Code", field: "postalCode" },
    { headerName: "Country", field: "country" },
    { headerName: "Phone Number", field: "phoneNumber" },
    { headerName: "Email", field: "email" },
  ];

  if (loading) {
    return (
      <Layout>
        <div className="container text-center">
          <h2>Loading users...</h2>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="container-fluid my-5">
        <h2>User List</h2>
        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

        {/* AG-Grid component */}
        <div className="ag-theme-alpine" style={{ height: 400, width: '100%' }}>
          <AgGridReact
            columnDefs={columnDefs}
            rowData={users}
            pagination={true}
            domLayout="autoHeight"
            enableBrowserTooltips={true}
          />
        </div>

        <Link to="/addUser" className="btn btn-success mt-3">
          Add User
        </Link>
      </div>
    </Layout>
  );
};

export default ViewUser;
