import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Pane, TextInputField, TextareaField, Button, Heading, Alert } from 'evergreen-ui';

const AddUser = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    address1: '',
    address2: '',
    city: '',
    postalCode: '',
    country: '',
    phoneNumber: '',
    email: '',
    userNotes: '',
  });

  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');

    try {
      await axios.post('/api/addUser', formData);
      setSuccessMessage('User added successfully!');
      setTimeout(() => navigate('/viewUser'), 2000);
    } catch (error) {
      setErrorMessage(
        error.response?.data || 'An error occurred while adding the user'
      );
    }
  };

  return (
    <Pane
      className="container"
      marginY={24}
      maxWidth={800}
      marginX="auto"
      padding={24}
      borderRadius={8}
      elevation={1}
      background="tint1"
    >
      <Pane display="flex" justifyContent="space-between" alignItems="center" marginBottom={16}>
        <Heading size={600}>Add User</Heading>
        <Button appearance="minimal" onClick={() => navigate('/viewUser')}>
          View Users
        </Button>
      </Pane>

      {errorMessage && (
        <Alert intent="danger" marginBottom={16} title="Error">
          {errorMessage}
        </Alert>
      )}
      {successMessage && (
        <Alert intent="success" marginBottom={16} title="Success">
          {successMessage}
        </Alert>
      )}

      <form onSubmit={handleSubmit}>
        {/* First Name and Last Name in the same row */}
        <Pane display="flex" gap={16}>
          <TextInputField
            flex={1}
            label="First Name"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            required
          />
          <TextInputField
            flex={1}
            label="Last Name"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            required
          />
        </Pane>

        {/* Other fields */}
        <TextInputField
          label="Date of Birth"
          type="date"
          name="dateOfBirth"
          value={formData.dateOfBirth}
          onChange={handleChange}
          required
        />
        <TextInputField
          label="Phone Number"
          name="phoneNumber"
          value={formData.phoneNumber}
          onChange={handleChange}
          required
        />
        <TextInputField
          label="Email"
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <TextInputField
          label="Address 1"
          name="address1"
          value={formData.address1}
          onChange={handleChange}
          required
        />
        <TextInputField
          label="Address 2"
          name="address2"
          value={formData.address2}
          onChange={handleChange}
        />
        <Pane display="flex" gap={16}>
          <TextInputField
            flex={1}
            label="City"
            name="city"
            value={formData.city}
            onChange={handleChange}
            required
          />
          <TextInputField
            flex={1}
            label="Postal Code"
            name="postalCode"
            value={formData.postalCode}
            onChange={handleChange}
            required
          />
        </Pane>
        <TextInputField
          label="Country"
          name="country"
          value={formData.country}
          onChange={handleChange}
          required
        />

        {/* User Notes in its own section */}
        <TextareaField
          label="User Notes"
          name="userNotes"
          value={formData.userNotes}
          onChange={handleChange}
          rows={3}
        />

        {/* Form Actions */}
        <Pane display="flex" justifyContent="flex-end" gap={16} marginTop={16}>
          <Button type="button" appearance="default" onClick={() => navigate('/viewUser')}>
            Cancel
          </Button>
          <Button type="submit" appearance="primary">
            Add User
          </Button>
        </Pane>
      </form>
    </Pane>
  );
};

export default AddUser;
