import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom'; // Updated: useNavigate
import Layout from './layout'; // Import Layout component

const UpdateUser = () => {
  const [user, setUser] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    phoneNumber: '',
    address1: '',
    address2: '',
    city: '',
    postalCode: '',
    country: '',
    email: '',
    userNotes: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const { id } = useParams(); // Get the user ID from the URL
  const navigate = useNavigate(); // Updated: useNavigate instead of useHistory

  // Fetch user data when the component mounts
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`/viewUser/${id}`);
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user:', error);
        setErrorMessage('Error fetching user data');
      }
    };

    fetchUserData();
  }, [id]);

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({
      ...prevUser,
      [name]: value
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/updateUser/${id}`, user); // Send the updated user data to the backend
      navigate('/viewUser'); // Updated: Redirect using navigate
    } catch (error) {
      console.error('Error updating user:', error);
      setErrorMessage('Error updating user');
    }
  };

  return (
    <Layout>
      <div className="form-container">
        <h2>Update User</h2>
        {errorMessage && (
          <div className="alert alert-danger">
            {errorMessage}
          </div>
        )}
        <form onSubmit={handleSubmit}>
          <div className="form-row">
            <div className="form-group col-md-6">
              <label htmlFor="firstName">First Name:</label>
              <input
                type="text"
                className="form-control"
                id="firstName"
                name="firstName"
                value={user.firstName}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group col-md-6">
              <label htmlFor="lastName">Last Name:</label>
              <input
                type="text"
                className="form-control"
                id="lastName"
                name="lastName"
                value={user.lastName}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group col-md-6">
              <label htmlFor="dateOfBirth">Date of Birth:</label>
              <input
                type="date"
                className="form-control"
                id="dateOfBirth"
                name="dateOfBirth"
                value={user.dateOfBirth.slice(0, 10)} // Format date as YYYY-MM-DD
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group col-md-6">
              <label htmlFor="phoneNumber">Phone Number:</label>
              <input
                type="text"
                className="form-control"
                id="phoneNumber"
                name="phoneNumber"
                value={user.phoneNumber}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group col-md-6">
              <label htmlFor="address1">Address 1:</label>
              <input
                type="text"
                className="form-control"
                id="address1"
                name="address1"
                value={user.address1}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group col-md-6">
              <label htmlFor="address2">Address 2:</label>
              <input
                type="text"
                className="form-control"
                id="address2"
                name="address2"
                value={user.address2}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group col-md-6">
              <label htmlFor="city">City:</label>
              <input
                type="text"
                className="form-control"
                id="city"
                name="city"
                value={user.city}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group col-md-6">
              <label htmlFor="postalCode">Postal Code:</label>
              <input
                type="text"
                className="form-control"
                id="postalCode"
                name="postalCode"
                value={user.postalCode}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group col-md-6">
              <label htmlFor="country">Country:</label>
              <input
                type="text"
                className="form-control"
                id="country"
                name="country"
                value={user.country}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group col-md-6">
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                className="form-control"
                id="email"
                name="email"
                value={user.email}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="userNotes">User Notes:</label>
            <textarea
              className="form-control"
              id="userNotes"
              name="userNotes"
              rows="4"
              value={user.userNotes}
              onChange={handleChange}
            ></textarea>
          </div>

          <div className="button-group text-center mt-4">
            <button type="submit" className="btn btn-primary">Update User</button>
            <a href="/viewUser" className="btn btn-success">View Users</a>
          </div>
        </form>
      </div>
    </Layout>
  );
};

export default UpdateUser;
